
//el express generator usa var
//1. importamos el modulo a utilizar. ES5
const express = require('express');

//2. Creamos una instancia de express
const app = express();
//con app. puedo utilizar todos los metodos de los modulos de express.

//4. Creamos una variable en el puerto.
const PORT = 9000;

//3306 es myqsl
//3000: create react app
//5200 algo es de vite
//4200 default angular
//27000 y algo es de mongodb
//9000 es tomcat
//3000 tambien es de spring

//Verbos http de express
//GET: Responder con el recurso solicitado.
//1. Ruta
//2. Funcion para responder a la peticion.
app.get('/',function(peticion,respuesta){

    console.log("=============================");

    console.log(peticion);
    console.log(peticion.url);
    console.log(peticion.method);
    console.log(peticion.headers);

    console.log("=============================");

    //respondemos a la peticion del cliente
    respuesta.send("Bienvenido al servidor");


});

app.get('/saludo', function(peticion,respuesta){
    respuesta.send("Hola mundo desde Node");
})


